- (UIButton *)<#button#> {

    if (<#button#> == nil) {

        <#button#> = [UIButton buttonWithType:UIButtonTypeCustom];

        [<#button#> setBackgroundColor:<#expression#>];
        [<#button#> setTitleColor:<#expression#> forState:UIControlStateNormal];
        [<#button#> setTitle:<#expression#> forState:UIControlStateNormal];
        <#button#>.titleLabel.font = [UIFont fontWithName:<#expression#> size:<#expression#>];
    }
    return <#button#>;
}
