- (UICollectionView *)<#UICollectionView#> {

    if (<#UICollectionView#> == nil) {

        UICollectionViewFlowLayout *flowLayout = [[UICollectionViewFlowLayout alloc]init];
        //设置2个item左右之间的最小间隙，
        flowLayout.minimumInteritemSpacing = CGFLOAT_MIN;
        //设置2个item上下之间的最小间隙
        flowLayout.minimumLineSpacing = CGFLOAT_MIN;
        <#UICollectionView#> = [[UICollectionView alloc]initWithFrame:CGRectZero collectionViewLayout:flowLayout];
        <#UICollectionView#>.delegate = self;
        <#UICollectionView#>.dataSource = self;
        <#UICollectionView#>.backgroundColor = [UIColor clearColor];
        <#UICollectionView#>.showsVerticalScrollIndicator = NO;

       [<#UICollectionView#> registerClass:[<#cell#> class] forCellWithReuseIdentifier:<#expression#>];

        [<#UICollectionView#> registerClass:[<#header#> class] forSupplementaryViewOfKind:UICollectionElementKindSectionHeader withReuseIdentifier:<#expression#>];
        [<#UICollectionView#> registerClass:[<#footer#> class] forSupplementaryViewOfKind:UICollectionElementKindSectionFooter withReuseIdentifier:<#expression#>];

    }
    return <#UICollectionView#>;
}
#pragma mark - UICollectionViewDataSource && UICollectionViewDelegate
- (NSInteger)numberOfSectionsInCollectionView:(UICollectionView *)collectionView {

    return <#expression#>
}
- (NSInteger)collectionView:(UICollectionView *)collectionView numberOfItemsInSection:(NSInteger)section {

    return <#expression#>
}
- (UICollectionViewCell *)collectionView:(UICollectionView *)collectionView cellForItemAtIndexPath:(NSIndexPath *)indexPath {

    <#UICollectionViewCell#> *cell = [collectionView dequeueReusableCellWithIdentifier:<#expression#>];
    return cell;
}
- (CGSize)collectionView:(UICollectionView *)collectionView layout:(UICollectionViewLayout*)collectionViewLayout sizeForItemAtIndexPath:(NSIndexPath *)indexPath {
    return <#expression#>
}
- (UICollectionReusableView *)collectionView:(UICollectionView *)collectionView viewForSupplementaryElementOfKind:(NSString *)kind atIndexPath:(NSIndexPath *)indexPath {
    if (kind == UICollectionElementKindSectionHeader) {

                 <#header#> *header = [collectionView dequeueReusableSupplementaryViewOfKind: UICollectionElementKindSectionHeader withReuseIdentifier:<#expression#> forIndexPath:indexPath];
        return header;
    }
    else if (kind ==  UICollectionElementKindSectionFooter) {
             <#footer#> *footer = [collectionView dequeueReusableSupplementaryViewOfKind:UICollectionElementKindSectionFooter withReuseIdentifier:<#expression#> forIndexPath:indexPath];
        return footer;
    }
    return  nil;
}
- (CGSize)collectionView:(UICollectionView *)collectionView layout:(UICollectionViewLayout*)collectionViewLayout referenceSizeForHeaderInSection:(NSInteger)section {

    return <#expression#>
}
- (CGSize)collectionView:(UICollectionView *)collectionView layout:(UICollectionViewLayout*)collectionViewLayout referenceSizeForFooterInSection:(NSInteger)section {

    return <#expression#>
}
